import React from 'react'

class OneProject extends React.Component{

    toggleClass(i){
        this.props.project.done ?  'done':  false;
    }
    render(){
        toggleClass(this.props.index)
        return (
        <li onClick={this.props.markToDone}>
            <div>Name: {this.props.project.name}</div>
            <div>Description: {this.props.project.description}</div>
            <button onClick={()=>this.props.removeProject(this.props.index)}>Delete</button>
        </li>
        )
    }
}

export default OneProject
