import React from 'react'
import './style.css'
class OneProject extends React.Component{
    constructor(props){
        super(props);
        this.save = this.save.bind(this);
        this.showEditBtn= this.showEditBtn(this);
        this.updateDiv=React.createRef();
      }

    save(index){
        var name=this.refs.edit-name.value;
        var description=this.refs.edit-description.value;
        this.props.editProject(index, {name, description});
    } 
    showEditBtn(){
        this.updateDiv.style.display="block";
    }
    render(){
        var classtoggle=this.props.project.done ?  'done':  'undone';
        return (
        <div>
        <div ref={this.updateDiv}>
                <input type="text" ref="edit-name" placeholder="name" /> <br />
                <input type="text" ref="edit-description"  placeholder="description" id="description"/> <br />
                <button onClick={()=>this.save(this.props.index)}>Save</button>
        </div>
        <div onClick={()=>this.props.markToDone(this.props.index)} className={classtoggle}>
            <div className="title">Name: </div>
            <div className="item-components"> {this.props.project.name}</div>
            <div className="title">Description:</div>
            <div className="item-components"> {this.props.project.description}</div>
            <button onClick={()=>this.props.removeProject(this.props.index)}>Delete</button>
            <button onClick={this.showEditBtn}>Edit</button>
        </div>
        </div>
        )
    }
}

export default OneProject
