import React from 'react'
import Form from './components/Form'
import ProjectsList from './components/ProjectsList'


 class Projects extends React.Component{
  constructor (props){
    super(props);
    this.addItem = this.addItem.bind(this);
    this.removeItem = this.removeItem.bind(this);
    this.editItem=this.editItem.bind(this);
    //this.markTodoDone = this.markTodoDone.bind(this);
    this.state={projects: this.props.projects};
  }
  //создаем
   addItem(projeect){
    this.props.projects.push({
      id: projeect.id,
      name: projeect.name,
      description: projeect.description,
      done: false
    });
    this.setState({projects: this.props.projects});
    }
    editItem(index){
      this.refs.name.value=this.props.projects[index].name;
    }
   removeItem(index){
      this.props.projects.splice(index, 1);
      this.setState({projects: this.props.projects});
    }
    render(){
      return(
        <div class="main">
        <Form addItem={this.addItem} editItem={this.editItem}/>
        <ProjectsList projects={this.props.projects} removeProject={this.removeItem}></ProjectsList>
        </div>
      )
    }
  }
  export default Projects
