import React from 'react'
import Form from './components/Form/Form'
import ProjectsList from './components/ProjectsList'


 class Projects extends React.Component{
  constructor (props){
    super(props);
    this.addItem = this.addItem.bind(this);
    this.removeItem = this.removeItem.bind(this);
    this.editItem=this.editItem.bind(this);
    this.markToDone = this.markToDone.bind(this);
    this.state={projects: this.props.projects};
  }

  //создаем
   addItem(projeect){
    this.props.projects.push({
      id: Math.random(),
      name: projeect.name,
      description: projeect.description,
      done: false
    });
    this.setState({projects: this.props.projects});
    }
    editItem(index){
      document.getElementById('#name').value=this.props.projects[index].name;
    }
   removeItem(index){
      this.props.projects.splice(index, 1);
      this.setState({projects: this.props.projects});
    }
    markToDone(index){
      var done=this.props.projects[index].done;
      this.props.projects[index].done=!done;
      this.setState({projects: this.props.projects});
    }
    render(){
      return(
        <div class="main">
        <Form addItem={this.addItem} editItem={this.editItem}/>
        <ProjectsList projects={this.props.projects} markToDone={this.markToDone} removeProject={this.removeItem} editProject={this.editItem}></ProjectsList>
        </div>
      )
    }
  }
  export default Projects
